import React from 'react';
import '../Profile/Profile.css';
import {connect} from 'react-redux';

const UserComponent = (props) => {
    return (
        <div className={'user-box'}>
            <div className={'user-box-avatar'}>
                <img src={props.avatar} className={'avatar'}/>
            </div>

            <div className={'user-box-information'}>
                <div className="user-name">
                    {props.firstName} {props.secondName}
                </div>
                <div>
                    День рождения: {props.birthday}
                </div>
                <div>
                    Город: {props.city}
                </div>
                <div>
                    Образование: {props.education}
                </div>
                <div>
                    Веб-сайт: {props.site}
                </div>
            </div>
        </div>
    );
};

const mapStateToProps = (state) => {
    return {
        firstName: state.profileData.users.firstName,
        secondName: state.profileData.users.secondName,
        birthday: state.profileData.users.birthday,
        city: state.profileData.users.city,
        education: state.profileData.users.education,
        site: state.profileData.users.site,
        avatar: state.profileData.users.avatar
    }
};
const ConnectedUserComponent = connect(mapStateToProps)(UserComponent);

export default ConnectedUserComponent;
