import {combineReducers, createStore} from "redux";
import avatar from "./Components/Profile/img/avatar.jpg";

//Сделать правильную копию объектов!!!
let initialStateForPost = {
    messageArray: [
        {avatar: avatar, post: "Эй, почему никто не лайкнул в прошлом посте?!"},
        {avatar: avatar, post: "Ну что?! Давайте лайкать теперь этот пост."}
    ],
    currentPost: ''
};
let initialStateForProfile = {
    users: [
        {avatar: avatar, firstName: 'Иван', secondName: 'Стечкин', birthday: '12 января 1990', city: 'Минск', education: 'ВКА им. Можайского', site: 'it-kamasutra.com'}
    ]
};

let initialStateForDialog = {
    dialogCorrespondents: [
        {avatar: avatar, firstName: 'Иван', id: 0},
        {avatar: 'http://www.foto4ka.com/wallpapers/7/angelina-jolie-poster-33447.jpg', firstName: 'Анджелина', id: 1}
    ],
    dialogMessages: [
        [],
        [{id: 0, message: 'Привет!'}, {id: 0, message: 'Чего не отвечаешь? Это Иван Стечкин из Олени Булз!'}, {id: 1, message: 'Аааа, бомжур, Иван! Зови даму в ресторан!'}],
    ],
    currentCorresspondentId: 2
};

const ADD_POST = 'NETWORK/PROFILE/CREATE_POSTS/ADD_POST';
const CHANGE_NEW_POST = 'NETWORK/PROFILE/CREATE_POSTS/CHANGE_NEW_POST';
const CHANGE_DIALOG = 'NETWORK/DIALOG/SHOW_DIALOG/CHANGE_DIALOG';

export const changeCurrentPost = (currentText) => {
    return {
        type: CHANGE_NEW_POST,
        newPost: currentText
    }
};
export const addPost = () => {
    return {
        type: ADD_POST
    }
};
export const changeDialogDispatch = (corresspondentId) => {
    return {
        type: CHANGE_DIALOG,
        currentCorresspondentId: corresspondentId
    }
};

const addPostReducer = (state = initialStateForPost, action) => {
    switch (action.type) {
        case ADD_POST:
            let stateCopy = {messageArray: [...state.messageArray]};
            stateCopy.messageArray.push({avatar: avatar, post: state.currentPost});
            stateCopy.currentPost = "";
            return stateCopy;
        case CHANGE_NEW_POST:
            let stateCopy2 = {...state};
            stateCopy2.currentPost = action.newPost;
            return stateCopy2;
        default:
            return state;
    }
};

const profilePageReducer = (state = initialStateForProfile, action) => {
    switch (action.type) {
        default:
            return state;
    }
};

const dialogPageReducer = (state = initialStateForDialog, action) => {
    switch (action.type) {
        case CHANGE_DIALOG:
            let stateCopy = {...state};
            state.currentCorresspondentId = action.currentCorresspondentId;
            return stateCopy;
        default:
            return state;
    }
};

const superReducer = combineReducers({
    message: addPostReducer,
    profileData: profilePageReducer,
    dialogData: dialogPageReducer
});


const store = createStore(superReducer);

export default store;
