import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';
import AppComponent from './Components/App/App';
import avatar from "./Components/Profile/img/avatar.jpg";

//state
let state = {
    message: {
        messageArray: [
            {avatar: avatar, post: "Эй, почему никто не лайкнул в прошлом посте?!"},
            {avatar: avatar, post: "Ну что?! Давайте лайкать теперь этот пост."}
        ],
        currentPost: ''
    },
    users: [
        {
            id: 0,
            avatarSrc: avatar,
            firstName: "Илья",
            secondName: "Муромец",
            birthday: "2 января",
            city: "Минск",
            education: "БГУ'11",
            site: "it-kamasutra.com"
        },
        {
            id: 1,
            avatarSrc: "https://thefancy-media-ec3.thefancy.com/UserImages/original/pian03_407f7e8f8b8d.jpg",
            firstName: "Настя",
            secondName: "Фокс",
            birthday: "2 января",
            city: "Минск",
            education: "БГУ'11",
            site: "it-kamasutra.com"
        },
        {
            id: 2,
            avatarSrc: "https://pbs.twimg.com/media/CT0Rp6pWEAEJcDI.jpg",
            firstName: "Кирилл",
            secondName: "Муромец",
            birthday: "2 января",
            city: "Минск",
            education: "БГУ'11",
            site: "it-kamasutra.com"
        },
        {
            id: 3,
            avatarSrc: "https://ssl-gfx.filmweb.pl/ph/47/92/684792/500470.2.jpg",
            firstName: "Мефодий",
            secondName: "Муромец",
            birthday: "2 января",
            city: "Минск",
            education: "БГУ'11",
            site: "it-kamasutra.com"
        }
    ],
    dialogs: {
        correspondents: [1, 2, 3],
        currentCorrespondentId: 1,
        messages: [
            [],
            [{id: 0, message: "Привет!"}, {id: 1, message: "Здарова, дружище!"}, {id: 0, message: "Как поживаешь?"}],
            [{id: 2, message: "Хай! Как дела?"}, {id: 0, message: "Привет! Все путем, скоро в отпуск."}]
        ]
    }
};

let dialogsCorrespondents ='';
const showDialogsCorrespondents = () => {
     dialogsCorrespondents = state.dialogs.correspondents.map((el) => {
         let dialogsCorrespondentsEl = {avatar: state.users[el].avatarSrc, firstName: state.users[el].firstName};
         return dialogsCorrespondentsEl;
     });
    renderAllPage();
};

const changeCurrentPost = (newPost) => {
    state.message.currentPost = newPost;
    renderAllPage();
};

const addPost = () => {
    state.message.messageArray = [...state.message.messageArray, {avatar: avatar, post: state.message.currentPost}];
    state.message.currentPost = "";
    renderAllPage();
};

const showDialog = (userName) => {

    switch(userName) {
        case 'Настя':
            state.dialogs.currentCorrespondentId = 1;
            break;

        case 'Кирилл':
            state.dialogs.currentCorrespondentId = 2;
            break;

        case 'Мефодий':
            state.dialogs.currentCorrespondentId = 3;
            break;
    };

};

let messages = state.dialogs.messages[state.dialogs.currentCorrespondentId];

const renderAllPage = () => {
    ReactDOM.render(<AppComponent addPost={addPost}
                                  changeCurrentPost={changeCurrentPost}
                                  defaultCurrentPost={state.message.currentPost}
                                  user={state.users[0]}
                                  showDialogsCorrespondents={showDialogsCorrespondents}
                                  dialogsCorrespondents={dialogsCorrespondents}
                                  messages={messages}
                                  showDialog={showDialog}
                                  posts={state.message.messageArray}/>, document.getElementById('page'));
};

renderAllPage();

serviceWorker.unregister();
