import React from 'react';
import './Header.css';
import logo from './img/mylogo.jpg';
import {Link} from "react-router-dom";

const Header = (logInState, userName) => {

    let logLink = "";
    if(logInState) {
        logLink = <div><span>Приветствуем, {userName}!</span><Link to={'/logOut'}>Выйти</Link></div>;
    }

    return (
        <div>
            <img src={logo} className={'logo'}/>
            {logLink}
        </div>
    )

}

export default Header;
