import React from 'react';
import './Dialogs.css';
import CorrespondentComponent from "./CorrespondentComponent";
import {connect} from 'react-redux';
import {Route} from "react-router";
import DialogsMessageComponent from "./DialogsMessage";
import {NavLink, withRouter} from "react-router-dom";
import DefaultDialogsMessageComponent from "./DefaultDialogsMessage";
import {chooseDialogOnClickDispatch, chooseDialogOnUrlDispatch} from "../../store";

const DialogsComponent = (props) => {
debugger;
    let idFromUrl = props.match.params.userId;
    let returnMessages = '';
    const params = props.location.pathname.split("/");

    let paramsHasUserId = params.length > 2 && props.dialogMessages[params[params.length - 1]];

    if (props.currentCorresspondentId || paramsHasUserId) {

        let detectedUserId = props.currentCorresspondentId || params[params.length - 1];

        returnMessages = props.dialogMessages[detectedUserId].map((el) => {
            return (<DialogsMessageComponent avatar={props.dialogCorrespondents[el.id].avatar}
                                             name={props.dialogCorrespondents[el.id].firstName}
                                             message={el.message}/>);
        });
    }
    // if(props.currentCorresspondentId) {
    //     returnMessages = props.dialogMessages[props.currentCorresspondentId].map((el) => {
    //         return (<DialogsMessageComponent avatar={props.dialogCorrespondents[el.id].avatar}
    //                                          name={props.dialogCorrespondents[el.id].firstName} message={el.message}/>);
    //     });
    // }
    let returnDialogCorrespondents = props.dialogCorrespondents.map((el) => {
        if (el.id !== 0) {
            return (<NavLink to={`/dialogs/${el.id}`}><CorrespondentComponent
                avatar={el.avatar} name={el.firstName} chooseDialog={props.chooseDialogOnClick}
                currentCorresspondentId={props.currentCorresspondentId}
                correspondentId={el.id}/>
            </NavLink>)
        }
    });

    return (
        <div className={'dialogs-box'}>
            <div className={'dialogs-titul'}>
                Диалоги
            </div>
            <div className={"dialogs-content"}>
                <div className={"dialogs-users"}>
                    {returnDialogCorrespondents}
                </div>
                <div className={"dialogs-messages"}>
                    <Route path={'/dialogs/:userId'} render={() => returnMessages}/>
                </div>
            </div>
        </div>
    );
};

const mapStateToProps = (state) => {
    return {
        dialogCorrespondents: state.dialogData.dialogCorrespondents,
        currentCorresspondentId: state.dialogData.currentCorresspondentId,
        dialogMessages: state.dialogData.dialogMessages
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        chooseDialogOnClick: (corresspondentId) => {
            dispatch(
                chooseDialogOnClickDispatch(corresspondentId)
            );
        },
        chooseDialogOnUrl: (corresspondentId) => {
            dispatch(
                chooseDialogOnUrlDispatch(corresspondentId)
            );
        }
    }
};

const ConnectedDialogComponent = withRouter(connect(mapStateToProps, mapDispatchToProps)(DialogsComponent));

export default ConnectedDialogComponent;
