import React, {Component} from 'react';
import {connect} from 'react-redux';
import Friends from "./Friends";
import {addNextFriendsThunk, friendsReturnThunk} from "../../Reducers/friendsReducer";

class FriendsContainer extends Component {

    componentWillMount(props) {
        this.props.friendsReturn()
    }

    render() {
        return <Friends {...this.props}/>
    }
}

const mapStateToProps = (state) => {
    return {
        friendsInfo: state.friends.friendsInfo,
        error: state.friends.error
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        friendsReturn: () => {
            dispatch(friendsReturnThunk())
        },
        addNextFriends: () => {
            dispatch(addNextFriendsThunk())
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(FriendsContainer)
