import React from 'react';
import '../Profile/Profile.css';
import {Redirect, withRouter} from "react-router";

const User = (props) => {

    let redirectToEditPage = (e) => {
        //e.preventDefault();
        props.history.push('/edit-profile');
    }

    return (
        <div className={'user-box'}>
            <div className={'user-box-avatar'}>
                <img src={props.avatar} className={'avatar'}/>
                <button className={'user-box-edit'} onClick={redirectToEditPage}>
                    Редактировать
                </button>
            </div>
            <div className={'user-box-information'}>
                <div className={'user-status'}>
                    {props.statusChangeFlag ? <div>
                            <div>
                                <textarea onChange={props.addCurrentStatusText} value={props.currentStatus}></textarea>
                            </div>
                            <div>
                                <button onClick={props.addNewStatus}>
                                    Изменить
                                </button>
                            </div>
                        </div>
                        :
                        <span onClick={props.changeStatus}>
                            {props.status}
                        </span>}
                </div>
                <div className="user-name">
                    Никнейм: {props.fullName}
                </div>
                <div>
                    Ищу ли работу: {props.lookingForAJob ? "Да" : "Нет"}
                </div>
                <div>
                    Описание поиска работы: {props.lookingForAJobDescription}
                </div>
                <div>
                    Обо мне: {props.aboutMe}
                </div>
                <div>
                    Контакты: <ul>
                    <li>{props.contacts.skype}</li>
                    <li>{props.contacts.vk}</li>
                    <li>{props.contacts.facebook}</li>
                    <li>{props.contacts.icq}</li>
                    <li>{props.contacts.email}</li>
                    <li>{props.contacts.googlePlus}</li>
                    <li>{props.contacts.twitter}</li>
                    <li>{props.contacts.instagram}</li>
                    <li>{props.contacts.whatsApp}</li>
                </ul>
                </div>
            </div>
        </div>
    );
};

export default withRouter(User);
