import React from 'react';
import './Header.css';
import logo from './img/mylogo.jpg';
import {Link} from "react-router-dom";

const Header = (props) => {
    console.log(props);
    return (
        <div>
            <img src={logo} className={'logo'}/>
            {props.logInState && <div><span>Приветствуем, {props.userInfo.userName}!</span><Link to={'/logOut'}>Выйти</Link></div>}
        </div>
    )

}

export default Header;
