import React from 'react';
import './LoginPage.css';
import {addCurrentLoginDispatch, addCurrentPasswordDispatch, loginSubmitClick} from "../../Reducers/loginPageReducer";
import {connect} from 'react-redux';
import {Redirect} from "react-router";

const Login = (props) => {

    if (props.LogInState) {
        return <Redirect to={"/"} />
    }

    let statusBarIconClass = "status-bar-icon-hide";
    if(props.statusBar) {
        statusBarIconClass = "status-bar-icon-show";
    }

    return (
        <div className={"content"}>
            <div className={"login-box"}>
                <div className="main-blocks">
                    <label htmlFor="email">Email:</label>
                    <input type="text" id="email" onChange={props.addCurrentLogin} value={props.currentLogin}/>
                </div>
                <div className="main-blocks">
                    <label htmlFor="password">Password:</label>
                    <input type="password" id="password" onChange={props.addCurrentPassword} value={props.currentPassword}/>
                </div>
                <div className="main-last-blocks">
                    <label htmlFor="checkbox" id="remember">
                        <input type="checkbox" className={"checkbox"}/>
                        remember me
                    </label>
                    <button id="login" onClick={props.onSubmitButtonClick}>Log In</button>
                    <img src="https://thumbs.gfycat.com/TinyMajesticAtlanticbluetang-size_restricted.gif" className={`statusBarIcon ${statusBarIconClass}`} alt=""/>
                </div>
            </div>
        </div>
            )
}

const mapStateToProps = (state) => {
    return {
        currentLogin: state.login.currentLogin,
        currentPassword: state.login.currentPassword,
        statusBar: state.login.statusBar,
        LogInState: state.auth.LogInState
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        addCurrentLogin: (e) => {
            dispatch(
                addCurrentLoginDispatch(e)
            );
        },
        addCurrentPassword: (e) => {
            dispatch(
                addCurrentPasswordDispatch(e)
            );
        },
        onSubmitButtonClick: () => {
            dispatch(
                loginSubmitClick()
            );
        }
    }
};

const ConnectedLogin = connect(mapStateToProps, mapDispatchToProps)(Login);

export default ConnectedLogin;
