import React from 'react';
import './Dialogs.css';

const DialogsListComponent = (props) => {

    return (
            <div className={"correspondent_box"} id={props.correspondentIdForProps}>
                    <div className={"correspondent_avatar"}>
                        <img src={props.avatar} />
                    </div>
                    <div className={"correspondent_name"}>
                        {props.name}
                    </div>
            </div>
    );
    console.log(props.correspondentIdForProps);

};

