import React from 'react';
import '../Profile/Profile.css';
import {connect} from 'react-redux';

const CreatePostsComponent = (props) => {
    let defaultCurrentPost = props.defaultCurrentPost;

    return (
        <div className={'createposts-box'}>
            <div className={'createposts-titul'}>
                Мои записи
            </div>
            <div>
                <textarea onChange={(e) => {
                    props.changePost(e.currentTarget.value);
                }} placeholder={"Что у вас нового..."} value={defaultCurrentPost}>
                </textarea>
            </div>
            <div>
                <button className={'createposts-button'} onClick={() => {
                    props.dispatch({type: 'ADD_POST'});
                }}>
                    Отправить
                </button>
            </div>
        </div>
    );
}

const mapStateToProps = (state) => {
    return {
        defaultCurrentPost: state.currentPost
    }
};
const mapDispatchToProps = (dispatch) => {
        return {
            changePost: (currentText) => {
            dispatch({
                type: 'CHANGE_NEW_POST',
                newPost: currentText
            });
        }
    }
};
const ConnectedCreatePostComponent = connect(mapStateToProps, mapDispatchToProps)(CreatePostsComponent);
export default ConnectedCreatePostComponent;
