import React from 'react'
import {reduxForm} from "redux-form";
import EditProfileForm from "./EditProfileForm";
import {connect} from "react-redux";
import {editProfileDataThunk} from "../../Reducers/profilePageReducer";
import avatar from "../Profile/img/avatar.jpg";


let FormUnderRedux = reduxForm({
    // a unique name for the form
    form: 'editProfile'
})(EditProfileForm)

class EditFormContainer extends React.Component {

    submit = (values) => {
        console.log(values)
        //необходимо создать объект из новых данных соответствующий стейту профайла, чтобы на сервер в положенном виде отправлялись данные
        let newData = {
            aboutMe: false,
            contacts: {
                skype: "skype",
                vk: "vk.com",
                facebook: "facebook",
                icq: "icq",
                email: "email",
                googlePlus: "gogep",
                twitter: "twitter",
                instagram: "instagra",
                whatsApp: "watsap"
            },
            photos: {
                small: null,
                large: avatar
            },
            status: "У вас нет статуса",
            lookingForAJob: false,
            lookingForAJobDescription: 'Ищу работу, знаю это это и это',
            fullName: null,
            statusChangeFlag: false,
            currentStatus: ""
        }
        this.props.editProfileData(values)
    }

    render() {
        return <FormUnderRedux onSubmit={this.submit} {...this.props} />
    }

}

const mapDispatchToProps = (dispatch) => {
    return {
        editProfileData: (newData) => {
            dispatch(editProfileDataThunk(newData))
        }
    }
}

export default connect(null, mapDispatchToProps)(EditFormContainer);
