import {connect} from 'react-redux';
import Friends from "./Friends";
import {addNextFriendsThunk, clearAfterLeaveCreation, friendsReturnThunk} from "../../Reducers/friendsReducer";

class FriendsContainer extends Component {

    constructor(props) {
        super(props)
        this.state = {
            search: ""
        }
    }

    editSearchFun = () => {
