import React from 'react';
import styles from './FriendsPage.module.css';
import {NavLink, withRouter} from "react-router-dom";


const Friends = (props) => {

    let friendsList = props.friendsInfo.map(el => {
        let status = null
        el.status ? status = el.status : status = 'Нет статуса'

        return <NavLink to={`/friends/${el.id}`} className={styles.friendLink}>
            <div className={styles.friendBox} key={el.id}>
                <div className={styles.friendAvatar}>
                    <img src={el.photos.large ? el.photos.large : "http://onapi.gob.do/images/avatar.png"} alt=""/>
                </div>
                <div className="friend-name">
                    {el.name}
                </div>
                <div className="friends-status">
                    {status}
                </div>
                <hr/>
            </div>
        </NavLink>
    })

    return (
        <div className={'page-box'}>
            <div>
                <input type={"text"} placeholder={"Поиск"} value={props.search} onChange={props.editSearch}/>
            </div>
            {friendsList}
            <div className="error">
                {props.error}
            </div>
            <button onClick={props.addNextFriends} disabled={props.error ? true : false}>Смотреть еще</button>
        </div>
    );
}

export default Friends;
