import React, {Component} from 'react';
import '../Profile/Profile.css';
import {connect} from 'react-redux';
import User from './User';
import {
    getStatusThunk,
    changeStatusCreator,
    addCurrentStatusTextCreator,
    addNewStatusThunk,
    getProfileInformationThunk
} from '../../Reducers/profilePageReducer';

class UserContainer extends Component {

    componentWillMount() {
        this.props.getStatus();
    }

    componentDidMount() {
        this.props.getProfileInformation()
    }

    render() {
        return <User {...this.props} />
            }

}

const mapStateToProps = (state) => {
    return {
        // firstName: state.profileData.users[0].firstName,
        // secondName: state.profileData.users[0].secondName,
        // birthday: state.profileData.users[0].birthday,
        // city: state.profileData.users[0].city,
        // education: state.profileData.users[0].education,
        // site: state.profileData.users[0].site,
        // avatar: state.profileData.users[0].avatar,
        status: state.profileData.status,
        statusChangeFlag: state.profileData.statusChangeFlag,
        currentStatus: state.profileData.currentStatus
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        getStatus: () => {
            dispatch (
                getStatusThunk()
            )
        },
        changeStatus: () => {
            dispatch (
                changeStatusCreator(true)
            )
        },
        addNewStatus: () => {
            dispatch (
                addNewStatusThunk()
            )
        },
        addCurrentStatusText: (e) => {
            dispatch (
                addCurrentStatusTextCreator(e)
            )
        },
        getProfileInformation: () => {
            dispatch (
                getProfileInformationThunk()
            )
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UserContainer);
