import React from 'react';
import avatar from "../Components/Profile/img/avatar.jpg";
import axios from "../Dall/axios-instance";
import {setLogInState, setMeData} from "./authReducer";

let initialStateForProfile = {
    users: [
        {
            avatar: avatar,
            firstName: 'Иван',
            secondName: 'Стечкин',
            birthday: '12 января 1990',
            city: 'Минск',
            education: 'ВКА им. Можайского',
            site: 'it-kamasutra.com',
            status: 'нет',
            id: null
        }
    ]
};

const SET_STATUS = 'NETWORK/PROFILE/SET_STATUS';
const SET_ID = 'NETWORK/PROFILE/SET_ID';

const profilePageReducer = (state = initialStateForProfile, action) => {
    let stateCopy = {...state};
    stateCopy.users = [...state.users];
    switch (action.type) {
        case SET_ID:
            stateCopy.users[0].id = action.id
            return stateCopy
        case SET_STATUS:
            stateCopy.users[0].status = action.status
            return stateCopy
        default:
            return state;
    }
};

export const getStatusThunk = () => {
    return (dispatch, getState) => {
        let state = getState();
        axios.get('auth/me')
            .then(result => {
                dispatch(setId(result.data.data.id));
                let userId = state.profileData.users[0].id;
                axios.get(`profile/status/${userId}`).then(
                    result => {
                        console.log(result);
                    }
                )
            })
    }
}

const setId = (idFromServer) => {
    return {
        type: SET_ID,
        id: idFromServer
    }
}

const setStatus = (statusFromServer) => {
    return {
        type: SET_STATUS,
        status: statusFromServer
    }
}

export default profilePageReducer;
