import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';
import AppComponent from './Components/App/App';
import avatar from "./Components/Profile/img/avatar.jpg";
import PostsComponent from "./Components/Profile/Posts";

//state
let state = {
    message: {
        messageArray : [
            {avatar: avatar, post: "Эй, почему никто не лайкнул в прошлом посте?!"},
            {avatar: avatar, post: "Ну что?! Давайте лайкать теперь этот пост."}
        ],
    }
};

let post1 = state.message.messageArray;
const addPost = (newPost) => {
    state.message.messageArray = [...state.message.messageArray, {avatar: avatar, post: newPost}];
    renderAllPage();
};

const renderAllPage = () => {
    ReactDOM.render(<AppComponent posts={post1} addPost={addPost} />, document.getElementById('page'));
};

renderAllPage();

serviceWorker.unregister();
