import React from 'react';
import './Dialogs.css';
import {chooseDialogDispatch} from "../../store";
import {connect} from 'react-redux';

const DialogsListComponent = (props) => {

    let currentCorrespondentClass = () => {
        if (props.correspondentId == props.currentCorresspondentId) {return ('currentCorrespondentClass')} else {return ('')}
    };

    console.log(currentCorrespondentClass());
    return (
            <div className={`correspondent_box ${currentCorrespondentClass()}`} id={props.correspondentId} onClick={props.chooseDialog}>
                    <div className={"correspondent_avatar"}>
                        <img src={props.avatar} />
                    </div>
                    <div className={"correspondent_name"}>
                        {props.name}
                    </div>
            </div>
    );
};

const mapDispatchToProps = (dispatch) => {
    return {
        chooseDialog: (corresspondentId) => {
            dispatch(
                chooseDialogDispatch(corresspondentId)
            );
        }
    }
};

const ConnectedDialogListComponent = connect(()=>{}, mapDispatchToProps)(DialogsListComponent);

export default ConnectedDialogListComponent;
