import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';
import AppComponent from './Components/App/App';
import avatar from "./Components/Profile/img/avatar.jpg";

//state
let state = {
    message: {
        messageArray: [
            {avatar: avatar, post: "Эй, почему никто не лайкнул в прошлом посте?!"},
            {avatar: avatar, post: "Ну что?! Давайте лайкать теперь этот пост."}
        ],
        currentPost: ''
    }
};

const changeCurrentPost = (newPost) => {
    state.message.currentPost = newPost;
    renderAllPage();
};

const addPost = () => {
    state.message.messageArray = [...state.message.messageArray, {avatar: avatar, post: state.message.currentPost}];
    state.message.currentPost = "";
    renderAllPage();
};

const renderAllPage = () => {
    ReactDOM.render(<AppComponent addPost={addPost} changeCurrentPost={changeCurrentPost}
                                  defaultCurrentPost={state.message.currentPost}
                                  posts={state.message.messageArray}/>, document.getElementById('page'));
};

renderAllPage();

serviceWorker.unregister();
