import React from 'react';
import './LoginPage.css';
import {addCurrentLoginDispatch, addCurrentPasswordDispatch, addCurrentCaptchaDispatch, loginSubmitClick} from "../../Reducers/loginPageReducer";
import {connect} from 'react-redux';
import {Redirect} from "react-router";

const Login = (props) => {

    if (props.logInState) {
        return <Redirect to={"/"} />
    }

    let errorMessageBlock = props.errorMessage &&  <p className={"error-message"}>{props.errorMessage}</p>;
    let statusBarBlock = props.statusBar && <img src="https://cdn2.scratch.mit.edu/get_image/user/16235410_60x60.png" className={"status-bar-icon"} alt=""/>
    let captchaBlock = props.captchaUrl && <div><img src={props.captchaUrl}/><input type="text" onChange={props.addCurrentCaptcha} value={props.currentCaptcha}/></div>

    return  <div className={"content"}>
                <div className={"login-box"}>
                    <div className="main-blocks">
                        <label htmlFor="email">Email:</label>
                        <input type="text" id="email" onChange={props.addCurrentLogin} value={props.currentLogin}/>
                    </div>
                    <div className="main-blocks">
                        <label htmlFor="password">Password:</label>
                        <input type="password" id="password" onChange={props.addCurrentPassword} value={props.currentPassword}/>
                    </div>
                    <div className="main-last-blocks">
                        <label htmlFor="checkbox" id="remember">
                            <input type="checkbox" className={"checkbox"}/>
                            remember me
                        </label>
                        <button id="login" onClick={props.onSubmitButtonClick}>Log In</button>
                        {statusBarBlock}
                    </div>
                    {errorMessageBlock}
                    {captchaBlock}
                </div>
            </div>
}

const mapStateToProps = (state) => {
    return {
        currentLogin: state.login.currentLogin,
        currentPassword: state.login.currentPassword,
        currentCaptcha: state.login.currentCaptcha,
        statusBar: state.login.statusBar,
        logInState: state.auth.logInState,
        errorMessage: state.login.errorMessage,
        captchaUrl: state.login.captchaUrl
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        addCurrentLogin: (e) => {
            dispatch(
                addCurrentLoginDispatch(e)
            );
        },
        addCurrentPassword: (e) => {
            dispatch(
                addCurrentPasswordDispatch(e)
            );
        },
        addCurrentCaptcha: (e) => {
            dispatch(
                addCurrentCaptchaDispatch(e)
            );
        },
        onSubmitButtonClick: () => {
            dispatch(
                loginSubmitClick()
            );
        }
    }
};

const ConnectedLogin = connect(mapStateToProps, mapDispatchToProps)(Login);

export default ConnectedLogin;
