import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';
import AppComponent from './Components/App/App';
import avatar from "./Components/Profile/img/avatar.jpg";
import {Provider} from "react-redux";
import store from './store';


let dialogsCorrespondents = '';
const showDialogsCorrespondents = () => {
    dialogsCorrespondents = state.dialogs.correspondents.map((el) => {
        let dialogsCorrespondentsEl = {avatar: state.users[el].avatarSrc, firstName: state.users[el].firstName};
        return dialogsCorrespondentsEl;
    });
    renderAllPage();
};

const changeCurrentPost = (newPost) => {
    state.message.currentPost = newPost;
    renderAllPage();
};

const showDialog = (userName) => {

    switch (userName) {
        case 'Настя':
            state.dialogs.currentCorrespondentId = 1;
            break;

        case 'Кирилл':
            state.dialogs.currentCorrespondentId = 2;
            break;

        case 'Мефодий':
            state.dialogs.currentCorrespondentId = 3;
            break;
    }
    ;
    renderAllPage();
};

const renderAllPage = () => {
    ReactDOM.render(<Provider><AppComponent addPost={addPost}
                                            changeCurrentPost={changeCurrentPost}
                                            defaultCurrentPost={state.message.currentPost}
                                            user={state.users[0]}
                                            showDialogsCorrespondents={showDialogsCorrespondents}
                                            dialogsCorrespondents={dialogsCorrespondents}
                                            messages={state.dialogs.messages[state.dialogs.currentCorrespondentId]}
                                            showDialog={showDialog}
                                            store={store}
                                            currentCorrespondentId={state.dialogs.currentCorrespondentId}
                                            posts={state.message.messageArray}/>
    </Provider>, document.getElementById('page'));
};

renderAllPage();

serviceWorker.unregister();
