import React, {Component} from 'react';
import {connect} from 'react-redux';
import Friends from "./Friends";
import {
    addNextFriendsThunk,
    changeSearchCreator,
    clearAfterLeaveCreation,
    friendsReturnThunk
} from "../../Reducers/friendsReducer";

class FriendsContainer extends Component {

    constructor (props) {
        super(props)
        this.state = {
            search: ""
        }
        this.changeSearchContFun = this.changeSearchContFun.bind(this)
    }

    componentDidMount(props) {
        this.props.friendsReturn()
    }

    componentWillUnmount() {
        this.props.clearAfterLeave()
    }

    changeSearchContFun (newText) {
        this.setState({search: newText})
    }

    render() {
        return <Friends {...this.props}/>
    }
}

const mapStateToProps = (state) => {
    return {
        friendsInfo: state.friends.friendsInfo,
        error: state.friends.error,
        searchText: state.friends.searchText
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        friendsReturn: () => {
            dispatch(friendsReturnThunk())
        },
        addNextFriends: () => {
            dispatch(addNextFriendsThunk())
        },
        clearAfterLeave: () => {
            dispatch(clearAfterLeaveCreation())
        },
        changeSearch: (e) => {
            dispatch(changeSearchCreator(e.target.value))
        },
        changeSearchCont: (e) => {
            this.changeSearchContFun(e.target.value)
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(FriendsContainer)
