import React from 'react';
import {getOneFriendInformation} from "../Dall/oneFriends-service";

let initialState = {
    fullName: null,
    userId: null,
    photos: {
        large: 'http://onapi.gob.do/images/avatar.png',
        small: 'http://onapi.gob.do/images/avatar.png'
    },
    contacts: {
        facebook: "не заполнено",
        github: "не заполнено",
        instagram: "не заполнено",
        mainLink: "не заполнено",
        twitter: "не заполнено",
        vk: "не заполнено",
        website: "не заполнено",
        youtube: "не заполнено"
    },
    aboutMe: "не заполнено",
    lookingForAJob: false,
    lookingForAJobDescription: "не заполнено",
}

const GET_FRIEND_INFORMATION = 'NETWORK/ONE_FRIEND/GET_FRIEND_INFORMATION'

export const oneFriendReducer = (state = initialState, action) => {
    let stateCopy = {...state}
    switch (action.type) {
        case GET_FRIEND_INFORMATION:
            stateCopy.fullName = action.newInformation.fullName
            stateCopy.userId = action.newInformation.userId
            stateCopy.photos = {...action.newInformation.photos}
            stateCopy.contacts = {...action.newInformation.contacts}
            stateCopy.aboutMe = action.newInformation.aboutMe
            stateCopy.lookingForAJob = action.newInformation.lookingForAJob
            stateCopy.lookingForAJobDescription = action.newInformation.lookingForAJobDescription
            return stateCopy
        default:
            return state
    }
}

export const getOneFriendThunk = (friendId) => {
    return async (dispatch, getState)=> {
        let newInformation = await getOneFriendInformation(friendId)
        console.log(newInformation)
        dispatch(getInformationCreator(newInformation))
    }
}

const getInformationCreator = (newInformation) => {
    return {
        type: GET_FRIEND_INFORMATION,
        newInformation
    }
}
