import React, { Component } from 'react';
import './calculator2.css';

class Calculator2 extends Component {

    constructor() {
        super();

        this.state = {
            number1: 5,
            number2: 7,
            result: 0
        };
    }

    editResult() {
        this.setState({result: this.state.number1 + this.state.number2});
    }

    render() {
    return (
        <div className="calculator-box">
            <div className="operations">
                <select name="operations">
                    <option value="plus">Сложение</option>
                    <option value="minus">Вычитание</option>
                    <option value="multiply">Умножение</option>
                    <option value="divide">Деление</option>
                </select>
            </div>
            <hr/>
            <div className="fields">
                <input type="number" id="number1" value={this.state.number1} />
                <input type="number" id="number2" value={this.state.number2}/>
            </div>
            <div className="result">
                <button id="count">Рассчитать</button>
                <span id="result">Результат: {this.state.result}</span>
            </div>
        </div>
    );
  }
} 

export default Calculator2;
