import React from 'react';
import './Dialogs.css';

const DialogsListComponent = (props) => {
    let showDialog = props.showDialog;

    const chooseDialog = (e) => {
        let userName = e.currentTarget.children[1].innerHTML;
        showDialog(userName);
    };

    return (
        <div className={"correspondent_box"} onClick={chooseDialog}>
            <div className={"correspondent_avatar"}>
                <img src={props.avatar} />
            </div>
            <div className={"correspondent_name"}>
                {props.name}
            </div>
        </div>
    );
}

export default DialogsListComponent;
