import React from 'react';
import './Dialogs.css';
import DialogsListComponent from "./DialogsList";
import {connect} from 'react-redux';
import {Route} from "react-router";
import DialogsMessageComponent from "./DialogsMessage";
import DefaultDialogsMessageComponent from "./DefaultDialogsMessage";
import {NavLink} from "react-router-dom";

const DialogsComponent = (props) => {

    let returnDialogCorrespondents = props.dialogCorrespondents.map((el) => {
        if(el.id !== 0) {
            return (<NavLink to={`/dialogs/${el.id}`} ><DialogsListComponent
                        avatar={el.avatar} name={el.firstName} correspondentId={el.id} />
                    </NavLink>)
        }
    });
    let returnMessages = '';
    if(props.currentCorresspondentId) {
            returnMessages = props.dialogMessages[props.currentCorresspondentId].map((el) => {
            return (<DialogsMessageComponent avatar={props.dialogCorrespondents[el.id].avatar}
                                             name={props.dialogCorrespondents[el.id].firstName} message={el.message}/>);
        });
    }



    // let returnMessages = '';
    // if (dialogsCorrespondents) {
    //     returnMessages = props.messages.map((el) => {
    //         let correspondentId = el.id;
    //         if (correspondentId === 0) {
    //             return <DialogsMessageComponent
    //                 avatar={"https://pp.userapi.com/c840638/v840638961/8a79b/qKKpiL6bDA4.jpg?ava=1"}
    //                 message={el.message}
    //                 name={"Я"}/>
    //         } else {
    //             --correspondentId;
    //             return <DialogsMessageComponent avatar={props.dialogsCorrespondents[correspondentId].avatar}
    //                                             message={el.message}
    //                                             name={props.dialogsCorrespondents[correspondentId].firstName}/>
    //         };
    //     });
    // };

    return (
        <div className={'dialogs-box'}>
            <div className="dialogs-titul">
                Диалоги
            </div>
            <div className="dialogs-content">
                <div className="dialogs-users">
                    {returnDialogCorrespondents}
                </div>
                <div className="dialogs-messages">
                    <Route path={`/dialogs`} render={() => <DefaultDialogsMessageComponent/> }/>
                    <Route path={`/dialogs/${props.currentCorresspondentId}`} render={() => returnMessages }/>
                </div>
            </div>
        </div>
    );
};

const mapStateToProps = (state) => {
    return {
        dialogCorrespondents: state.dialogData.dialogCorrespondents,
        currentCorresspondentId: state.dialogData.currentCorresspondentId,
        dialogMessages: state.dialogData.dialogMessages
    }
};

const ConnectedDialogComponent = connect(mapStateToProps)(DialogsComponent);

export default ConnectedDialogComponent;
