import React from 'react';
import '../Profile/Profile.css';
import {connect} from 'react-redux';
import {changeCurrentPost, addPost} from '../../Reducers/addPostReducer';

const CreatePosts = (props) => {
    return (
        <div className={'createposts-box'}>
            <div className={'createposts-titul'}>
                Мои записи
            </div>
            <div>
                <textarea onChange={(e) => {
                    props.changePost(e.currentTarget.value);
                }} placeholder={"Что у вас нового..."} value={props.defaultCurrentPost}>
                </textarea>
            </div>
            <div>
                <button className={'createposts-button'} onClick={props.addPost}>
                    Отправить
                </button>
            </div>
        </div>
    );
}

const mapStateToProps = (state) => {
    return {
        defaultCurrentPost: state.message.currentPost
    }
};
const mapDispatchToProps = (dispatch) => {
    return {
        changePost: (currentText) => {
            dispatch(
                changeCurrentPost(currentText)
            );
        },
        addPost: () => {
            dispatch(
                addPost()
            );
        }
    }
};
const ConnectedCreatePosts = connect(mapStateToProps, mapDispatchToProps)(CreatePosts);
export default ConnectedCreatePosts;
