import React from 'react';
import avatar from "../Components/Profile/img/avatar.jpg";
import axios from "../Dall/axios-instance";

let initialStateForProfile = {
    users: [
        {
            avatar: avatar,
            firstName: 'Иван',
            secondName: 'Стечкин',
            birthday: '12 января 1990',
            city: 'Минск',
            education: 'ВКА им. Можайского',
            site: 'it-kamasutra.com',
            status: null,
            id: null
        }
    ],
    statusChangeFlag: false,
    currentStatus: ""
};

const SET_STATUS = 'NETWORK/PROFILE/SET_STATUS';
const SET_ID = 'NETWORK/PROFILE/SET_ID';
const CHANGE_STATUS = 'NETWORK/PROFILE/CHANGE_STATUS';
const ADD_CURRENT_STATUS_TEXT = 'NETWORK/PROFILE/ADD_CURRENT_STATUS_TEXT';

const profilePageReducer = (state = initialStateForProfile, action) => {
    let stateCopy = {...state};
    stateCopy.users = [...state.users];
    switch (action.type) {
        case SET_ID:
            stateCopy.users[0].id = action.id
            return stateCopy
        case SET_STATUS:
            stateCopy.users[0].status = action.status
            return stateCopy
        case CHANGE_STATUS:
            stateCopy.statusChangeFlag = action.flag
            return stateCopy
        case ADD_CURRENT_STATUS_TEXT:
            stateCopy.currentStatus = action.textareaObject.value
            console.log(stateCopy.currentStatus)
            return stateCopy
        default:
            return state;
    }
};

//Thunks

export const getStatusThunk = () => {
    return (dispatch, getState) => {
        let state = getState();
        axios.get('auth/me')
            .then(result => {
                dispatch(setId(result.data.data.id));
                let userId = state.profileData.users[0].id;
                axios.get(`profile/status/${userId}`).then(
                    result => {
                        dispatch(setStatus(result.data));
                        console.log(result)
                        axios.put('profile/status', {status: "Новый текст"});
                    }
                )
            })
    }
}

export const addNewStatusThunk = () => {

}

//action creators

const setId = (idFromServer) => {
    return {
        type: SET_ID,
        id: idFromServer
    }
}

const setStatus = (statusFromServer) => {
    return {
        type: SET_STATUS,
        status: statusFromServer
    }
}

export const changeStatusCreator = (flag) => ({
    type: CHANGE_STATUS,
    flag: flag
})

export const addCurrentStatusTextCreator = (e) => ({
    type: ADD_CURRENT_STATUS_TEXT,
    textareaObject: e
})

export default profilePageReducer;
