import React from 'react';
import '../Profile/Profile.css';

const User = (props) => {

    return (
        <div className={'user-box'}>
            <div className={'user-box-avatar'}>
                <img src={props.avatar} className={'avatar'}/>
            </div>

            <div className={'user-box-information'}>
                <div className={'user-status'}>
                    {props.statusChangeFlag ? <textarea>Привет</textarea> : props.status}
                </div>
                <div className="user-name">
                    {props.firstName} {props.secondName}
                </div>
                <div>
                    День рождения: {props.birthday}
                </div>
                <div>
                    Город: {props.city}
                </div>
                <div>
                    Образование: {props.education}
                </div>
                <div>
                    Веб-сайт: {props.site}
                </div>
            </div>
        </div>
    );
};

export default User;
