import React from 'react';
import './DialogsPage.css';
import Sidebar from "../Sidebar/Sidebar";
import Header from "../Header/Header";
import Dialogs from "../Dialogs/Dialogs";
import {Redirect} from "react-router";
import {connect} from 'react-redux';


const DialogsPage = (props) => {

    if (!props.LogInState) {
        return <Redirect to={"/login"} />
    }

    if (props.match.params.userId) {
        let idFromUrl = props.match.params.userId;
        if (idFromUrl != props.currentCorresspondentId) {
            props.chooseDialogOnUrl(idFromUrl);
        }
    }

    return (
        <div className={'page-box'}>
            <div className={'header'}>
                <Header/>
            </div>
            <div className="content-wrapper">
                <div className={'sidebar-block'}>
                    <Sidebar/>
                </div>
                <div className={'content'}>
                    <Dialogs />
                </div>
            </div>
        </div>
    );
}

const mapStateToProps = (state) => {
    return {
        LogInState: state.auth.LogInState,
        currentCorresspondentId: state.dialogData.currentCorresspondentId
    }
};

const ConnectedDialogsPage = connect(mapStateToProps, null)(DialogsPage);

export default ConnectedDialogsPage;
