import React from 'react';
import '../Profile/Profile.css';

const CreatePostsComponent = (props) => {
    let addPost = props.addPost;
    let createNewPostOnButton = ()=> {

        addPost("Привет!");
    };

    return (
        <div className={'createposts-box'}>
            <div className={'createposts-titul'}>
                Мои записи
            </div>
            <div>
                <textarea name="" id="">
                    Что у вас нового...
                </textarea>
            </div>
            <div>
                <button className={'createposts-button'} onClick={createNewPostOnButton}>
                    Отправить
                </button>
            </div>
        </div>
    );
}

export default CreatePostsComponent;
