import React from 'react';
import '../Profile/Profile.css';

const CreatePostsComponent = (props) => {
    let changeCurrentPost = props.changeCurrentPost;
    //let addPost = props.addPost;
    this.textInput = React.createRef();
    this.createNewPostOnButton = this.createNewPostOnButton.bind(this);


    let createNewPostOnButton = (e)=> {
        //addPost();
        alert (this.textInput);
    };

    let onChangeNewPost = (e) => {
        changeCurrentPost(e.currentTarget.value);
    };

    return (
        <div className={'createposts-box'}>
            <div className={'createposts-titul'}>
                Мои записи
            </div>
            <div>
                <textarea onChange={onChangeNewPost} placeholder={"Что у вас нового..."} ref={(input) => { this.textInput = input; }}>

                </textarea>
            </div>
            <div>
                <button className={'createposts-button'} onClick={this.createNewPostOnButton}>
                    Отправить
                </button>
            </div>
        </div>
    );
}

export default CreatePostsComponent;
