import React from 'react';
import axios from "../Dall/axios-instance";

let initialStateForFriends = {
    friendsInfo: [],
    currentPage: 1
};

const SAVE_FRIENDS_INFO = 'NETWORK/FRIENDS/SAVE_FRIENDS_INFO';

const friendsReducer = (state = initialStateForFriends, action) => {
    let stateCopy = {...state}
    stateCopy.friendsInfo = state.friendsInfo
    switch (action.type) {
        case SAVE_FRIENDS_INFO:
            stateCopy.friendsInfo = action.newFriendsInfo
            return stateCopy
        default:
            return state;
    }
};

export const friendsReturnThunk = () => {
    return (dispatch, getState) => {
        let state = getState();
        axios.get(`users?count=3&page=${state.friends.currentPage}`).then(
            result => {
                dispatch(newFriendsInfo(result.data.items))
            }
        )
    }
}

export const addNextFriendsThunk = () => {
    return
}

//action creaters

const newFriendsInfo = (newFriendsArray) => {
    return {
        type: SAVE_FRIENDS_INFO,
        newFriendsInfo: newFriendsArray
    }
}

export default friendsReducer;
