import React from 'react';
import './Dialogs.css';
import {chooseDialogDispatch} from "../../store";
import {connect} from 'react-redux';

const DialogsListComponent = (props) => {

    let currentCorrespondentClass = () => {
        if (props.correspondentId === props.currentCorresspondentId) {return true} else {return false}
    };
    if (currentCorrespondentClass()) {currentCorrespondentClass = 'currentCorrespondentClass'} else {currentCorrespondentClass = ''};
    alert(props.currentCorresspondentId);
    return (
            <div className={"correspondent_box"} className={currentCorrespondentClass} id={props.correspondentId} onClick={props.chooseDialog}>
                    <div className={"correspondent_avatar"}>
                        <img src={props.avatar} />
                    </div>
                    <div className={"correspondent_name"}>
                        {props.name}
                    </div>
            </div>
    );
};

const mapDispatchToProps = (dispatch) => {
    return {
        chooseDialog: (corresspondentId) => {
            dispatch(
                chooseDialogDispatch(corresspondentId)
            );
        }
    }
};

const mapStateToProps = (state) => {
    return {
        currentCorresspondentId: state.dialogData.currentCorresspondentId
    }
};
const ConnectedDialogListComponent = connect(mapStateToProps, mapDispatchToProps)(DialogsListComponent);

export default ConnectedDialogListComponent;
