import {combineReducers, createStore} from "redux";
import avatar from "./Components/Profile/img/avatar.jpg";

let initialStateForPost = {
    messageArray: [
        {avatar: avatar, post: "Эй, почему никто не лайкнул в прошлом посте?!"},
        {avatar: avatar, post: "Ну что?! Давайте лайкать теперь этот пост."}
    ],
    currentPost: ''
};
const addPostReducer = (state = initialStateForPost, action) => {
    switch (action.type) {
        case 'ADD_POST':
            state.messageArray.push({avatar: avatar, post: state.currentPost});
            state.currentPost = "";
            return state;
        case 'CHANGE_NEW_POST':
            state.currentPost = action.newPost;
            return state;
        case 'SHOW_ALL_POSTS':
            let posts = state.messageArray.map((el) => {
                return <div className={'posts-box'}>
                    <div className="posts-avatar">
                        <img src={el.avatar} />
                    </div>
                    <div className="posts-message">
                        {el.post}
                    </div>
                </div>
            });
            return posts;
        default:
            return state;
    }
};
const superReducer = combineReducers({
    message: addPostReducer
});


const store = createStore(superReducer);

export default store;
