import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';
import AppComponent from './Components/App/App';

//state
let state = {
    message: {
        messageArray : ['Hello', 'World'],
        }
};


const addMessage = (message) => {
    state.message.messageArray = [...state.message.messageArray, message];
    renderAllPage();
};

const renderAllPage = () => {
    ReactDOM.render(<AppComponent stateFromIndex={state.message} functionAddMessage={addMessage} />, document.getElementById('page'));
};

renderAllPage();

serviceWorker.unregister();
