import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';
import AppComponent from './Components/App/App';

//state
let state = {
    massage: {
        massageArray : ['Hello', 'World'],
        }
};


const addMassage = () => {
    alert(state.massage.massageArray[0]);
};

const renderAllPage = () => {
    ReactDOM.render(<AppComponent stateFromIndex={state.massage} functionAddMassage={addMassage} />, document.getElementById('page'));
};

renderAllPage();

serviceWorker.unregister();
