import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import * as serviceWorker from './serviceWorker';
import AppComponent from './Components/App/App';
import avatar from "./Components/Profile/img/avatar.jpg";
import PostsComponent from "./Components/Profile/Posts";

//state
let state = {
    message: {
        messageArray : [
            {avatar: avatar, post: "Эй, почему никто не лайкнул в прошлом посте?!"},
            {avatar: avatar, post: "Ну что?! Давайте лайкать теперь этот пост."}
        ],
    }
};

let posts = state.message.messageArray.map((el)=> {
    return <PostsComponent avatar={el.avatar} message={el.message}/>
});


const addMessage = (message) => {
    state.message.messageArray = [...state.message.messageArray, message];
    renderAllPage();
};

const renderAllPage = () => {
    ReactDOM.render(<AppComponent posts={posts} functionAddMessage={addMessage} />, document.getElementById('page'));
};

renderAllPage();

serviceWorker.unregister();
