import React from 'react';
import './Header.css';
import logo from './img/mylogo.jpg';
import {Link} from "react-router-dom";

const Header = ({logInState, userInfo}) => {

    return (
        <div>
            <img src={logo} className={'logo'}/>
            {logInState && <div><span>Приветствуем, {userInfo.userName}!</span><Link to={"/login"} onClick={props.logOut}>Выйти</Link></div>}
        </div>
    )

}

export default Header;
