import React from 'react';
import './Dialogs.css';

const DialogsListComponent = (props) => {

    return (
            <div onClick={chooseDialog} className={"correspondent_box"}>
                    <div className={"correspondent_avatar"}>
                        <img src={props.avatar} />
                    </div>
                    <div className={"correspondent_name"}>
                        {props.name}
                    </div>
                    <div>
                        {props.correspondentId}
                    </div>
            </div>
    );
};

const chooseDialog = (e) => {
    //let corresspondentId = e.currentTarget.children[1].innerHTML;
    alert (props.name);
};

//Id сохраняется для каждой отрисованной компоненты. Можно сразу обращаться к ней через props.id
export default DialogsListComponent;
