import React from 'react';
import './Dialogs.css';
import DialogsListComponent from "./DialogsList";
import DialogsMessageComponent from "./DialogsMessage";

const DialogsComponent = (props) => {

    let showDialogsCorrespondents = props.showDialogsCorrespondents;
    let dialogsCorrespondents = props.dialogsCorrespondents;
    if(!dialogsCorrespondents) {
        showDialogsCorrespondents();
    } else {
            dialogsCorrespondents = dialogsCorrespondents.map((el) => {
            return <DialogsListComponent showDialog={props.showDialog} avatar={el.avatar} name={el.firstName}/>
        });
    };

    let messages = props.messages;
    for (let i = 1; i < messages.length; i++) {
        let correspondentId = messages[i].id;
        return <DialogsMessageComponent messages={messages[i].message} avatar={dialogsCorrespondents[correspondentId].avatar} name={dialogsCorrespondents[correspondentId].firstName}/>
    }

    return (
        <div className={'dialogs-box'}>
            <div className="dialogs-titul">
                Диалоги
            </div>
            <div className="dialogs-content">
                <div className="dialogs-users">
                    {dialogsCorrespondents}
                </div>
                <div className="dialogs-messages">
                    <DialogsMessageComponent messages={props.messages} />
                </div>
            </div>
        </div>
    );
}

export default DialogsComponent;
