import React from 'react';
import '../Profile/Profile.css';
import wallpaper from './img/wallpaper.jpg';
import avatar from './img/avatar.jpg';
import UserComponent from "./User";
import CreatePostsComponent from "./CreatePosts";
import PostsComponent from "./Posts";

const ProfileComponent = (props) => {

    let oneUser = {
        avatarSrc : avatar,
        firstName : "Илья",
        secondName : "Муромец",
        birthday: "2 января",
        city: "Минск",
        education: "БГУ'11",
        site: "it-kamasutra.com"
    };

    let posts = props.posts.map((el)=> {
        return <PostsComponent avatar={el.avatar} post={el.post}/>
    });

    return (
        <div>
            <img src={wallpaper} className={'wallpaper'}/>
            <UserComponent
                avatarSrc={oneUser.avatarSrc}
                firstName={oneUser.firstName}
                secondName={oneUser.secondName}
                birthday={oneUser.birthday}
                city={oneUser.city}
                education={oneUser.education}
                site={oneUser.site}/>
            <CreatePostsComponent changeCurrentPost={props.changeCurrentPost} currentPost={props.currentPost} addPost={props.addPost}/>
            <div className="posts-box-wrapper">
                {posts}
            </div>
        </div>
    );
}

export default ProfileComponent;
