import React from 'react';
import '../Profile/Profile.css';
import {connect} from 'react-redux';

const CreatePostsComponent = (props) => {
    return (
        <div className={'createposts-box'}>
            <div className={'createposts-titul'}>
                Мои записи
            </div>
            <div>
                <textarea onChange={(e) => {
                    props.changePost(e.currentTarget.value);
                }} placeholder={"Что у вас нового..."} value={props.defaultCurrentPost}>
                </textarea>
            </div>
            <div>
                <button className={'createposts-button'} onClick={props.addPost}>
                    Отправить
                </button>
            </div>
        </div>
    );
}

const mapStateToProps = (state) => {
    return {
        defaultCurrentPost: state.message.currentPost
    }
};
const mapDispatchToProps = (dispatch) => {
        return {
            changePost: (currentText) => {
                dispatch({
                    type: 'CHANGE_NEW_POST',
                    newPost: currentText
                });
            },
            addPost: () => {
                dispatch({
                    type: 'ADD_POST'
                });
            }


    }
};
const ConnectedCreatePostComponent = connect(mapStateToProps, mapDispatchToProps)(CreatePostsComponent);
export default ConnectedCreatePostComponent;
